+    <script>
+        document.addEventListener('DOMContentLoaded', function () {
+            // –ê–Ω–∏–º–∞—Ü–∏—è –¥–ª—è –∫–∞—Ä—Ç–æ—á–µ–∫ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ
+            const cards = document.querySelectorAll('.system-card');
+            cards.forEach((card, index) => {
+                card.style.opacity = '0';
+                card.style.transform = 'translateY(20px)';
+
+                setTimeout(() => {
+                    card.style.transition = 'all 0.5s ease';
+                    card.style.opacity = '1';
+                    card.style.transform = 'translateY(0)';
+                }, 300 + (index * 200));
+            });
+
+            // –ê–Ω–∏–º–∞—Ü–∏—è –¥–ª—è —ç–ª–µ–º–µ–Ω—Ç–æ–≤ –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–∏
+            const configItems = document.querySelectorAll('.config-item');
+            configItems.forEach(item => {
+                item.addEventListener('mouseenter', function () {
+                    this.style.transform = 'translateY(-3px)';
+                    this.style.boxShadow = '0 8px 20px rgba(0,0,0,0.08)';
+                });
+
+                item.addEventListener('mouseleave', function () {
+                    this.style.transform = 'translateY(0)';
+                    this.style.boxShadow = 'none';
+                });
+            });
+
+            // –ê–Ω–∏–º–∞—Ü–∏—è –¥–ª—è features –≤ header
+            const features = document.querySelectorAll('.feature');
+            features.forEach((feature, index) => {
+                feature.style.opacity = '0';
+                feature.style.transform = 'translateY(15px)';
+
+                setTimeout(() => {
+                    feature.style.transition = 'all 0.5s ease';
+                    feature.style.opacity = '1';
+                    feature.style.transform = 'translateY(0)';
+                }, 500 + (index * 150));
+            });
+
+            // –°–æ—Ö—Ä–∞–Ω—è–µ–º –≤—ã–±–æ—Ä —Å–∏—Å—Ç–µ–º—ã –≤ localStorage
+            const systemLinks = document.querySelectorAll('.btn-lxt, .btn-xgt');
+            systemLinks.forEach(link => {
+                link.addEventListener('click', function () {
+                    const system = this.classList.contains('btn-lxt') ? 'LXT' : 'XGT';
+                    localStorage.setItem('lastSystem', system);
+                    localStorage.setItem('lastSystemTime', new Date().toISOString());
+                });
+            });
+
+            // –ü–æ–∫–∞–∑—ã–≤–∞–µ–º –ø–æ—Å–ª–µ–¥–Ω—é—é –≤—ã–±—Ä–∞–Ω–Ω—É—é —Å–∏—Å—Ç–µ–º—É
+            const lastSystem = localStorage.getItem('lastSystem');
+            const lastSystemTime = localStorage.getItem('lastSystemTime');
+
+            if (lastSystem && lastSystemTime) {
+                const timeDiff = (new Date() - new Date(lastSystemTime)) / (1000 * 60 * 60 * 24); // –†–∞–∑–Ω–∏—Ü–∞ –≤ –¥–Ω—è—Ö
+                if (timeDiff < 7) { // –ü–æ–∫–∞–∑—ã–≤–∞—Ç—å —Ç–æ–ª—å–∫–æ –µ—Å–ª–∏ –≤—ã–±—Ä–∞–Ω–æ –≤ —Ç–µ—á–µ–Ω–∏–µ –ø–æ—Å–ª–µ–¥–Ω–µ–π –Ω–µ–¥–µ–ª–∏
+                    console.log(`–ü–æ—Å–ª–µ–¥–Ω—è—è –≤—ã–±—Ä–∞–Ω–Ω–∞—è —Å–∏—Å—Ç–µ–º–∞: ${lastSystem} (${Math.round(timeDiff)} –¥–Ω–µ–π –Ω–∞–∑–∞–¥)`);
+                }
