+            --sidebar-collapsed-width: 70px;
+            --primary-color: #2c3e50;
+            --secondary-color: #34495e;
+            --accent-color: #3498db;
+            --text-color: #333;
+            --light-text: #ecf0f1;
+            --warning-color: #ff9800;
