+            const modalImg = modal.querySelector('.modal-content');
+            const closeModal = modal.querySelector('.close');
+
+            // –û–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ –∑–∞–∫—Ä—ã—Ç–∏—è
+            closeModal.onclick = function() {
+                modal.style.display = 'none';
+            };
+            
+            modal.onclick = function() {
+                modal.style.display = 'none';
